package _01_basic;

public class _04_ {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//4. 10 ~ 1까지 거꾸로 do ~ while문으로 출력
		
		
		int i = 10;
		
		do { System.out.println(i); 
			i--;
			
		} while (i >= 1);
	}
}



